import './App.css';
import React , { useState } from 'react';

function App() {
  const [contact,setContact]=useState({
    firstname:"Jhon",
    lstname:"Doe",
    phone:"+212627709544",
    email:"driss.lakhzani.ds@gmail.com",
    isFavorite:true
  })
  function toggleFavorite() {
    setContact(prevContact=>{ 
      return{...prevContact,isFavorite:!prevContact.isFavorite}})
    console.log(contact.isFavorite) 
  }
  return (
    <main>
            <article className="card">
                <img src={"./images/Intern.JPG"} alt="#" className="card--image" />
                <div className="card--info">
                    <img 
                        src={contact.isFavorite?"./images/start.png":"./images/empty-star.png"} 
                        className="card--favorite"
                        onClick={toggleFavorite}
                        alt='#'
                    />
                    <h2 className="card--name">
                        {contact.firstname} {contact.lstname}
                    </h2>
                    <p className="card--contact">{contact.phone}</p>
                    <p className="card--contact">{contact.email}</p>
                </div>

            </article>
        </main>
  )
};

export default App;
