import React,{useEffect, useState} from "react";
import Button from '@mui/material/Button';
// import Image from './images/comm-img.png'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {  faFontAwesome, faFacebookSquare,faLinkedin,faTwitterSquare,faGithubSquare,faStackOverflow  } from '@fortawesome/free-brands-svg-icons'
import { library } from '@fortawesome/fontawesome-svg-core'
import { fas  } from '@fortawesome/free-solid-svg-icons'
import { faEnvelope , faComment , faRepeat , faHeart , faBarsStaggered,faArrowUpFromBracket } from '@fortawesome/free-solid-svg-icons'
library.add(fas, faFontAwesome);



// for users to post tweets.

function TweetForm(props){  
    let like=props.likes
    return(
        <section className="bg-black text-gray-500 h-screen w-screen flex justify-center ">
            <img src={"#"} alt="#" />
            <div className="w-2/5 flex-col items-center justify-center ">
                <div className="flex flex-row">
                    <h3 className="pr-4 pl-1">{props.username}</h3>   {/*profile name*/}
                    <span className="flex" ><p>{props.tag}</p><p className="pl-3">. 23h</p></span>
                </div>
                
                <span className="text-white pt-2 ">{props.description}</span>
                    <img className="flex justify-center border-r-1.4 pt-2" src={props.img} alt="#" />
                    <div>  {/* this is for the comment section*/}

                    </div>
                    <div className="text-white-700 text-sm flex my-1  justify-between  ">
                        <span className="flex items-center w-14 px-3 py-2  justify-space "  >
                            <FontAwesomeIcon icon={faComment} />{props.comments}
                        </span>
                        <span className="flex items-center w-14 px-3 py-2  justify-space ">
                            <FontAwesomeIcon icon={faRepeat} />{props.retweets}
                        </span>
                        <span className="flex items-center w-14 px-3 py-2  justify-space ">
                            <FontAwesomeIcon icon={faHeart} />{like >= 10000 ? `${(like / 1000).toFixed(1)}K` : like}
                        </span>
                        <span className="flex items-center w-14 px-3 py-2  justify-space ">
                            <FontAwesomeIcon icon={faBarsStaggered} rotation={270} />{props.stats}
                        </span>
                        <span className=" items-center w-4 mx-8 pr-5 pl-3 py-2  ">
                            <FontAwesomeIcon icon={faArrowUpFromBracket} />
                        </span>
                    </div>
                    
            </div>
        </section>
    )
};

export default TweetForm
