import React, { useState } from 'react'
import list from './data'
import Resulte from './Result'


function SearchBar() {
    const [cherche,setData]=useState({search:""})
    const [filtered,setFiltered]=useState([])
      console.log(...cherche.search)

      //for filtring the data & select the chosen ones
    function handleFilter(){
       const listes= list.filter((a)=>a.type==cherche.search)
       setFiltered(listes)
    }
    return (
      <form className='px-4 bg-blue-800 h-2/4 w-1/5 text-white text rounded-lg' >
          <h1 className=' text-2xl text-gray-400'>SearchBar</h1>
          <input  type="text" name='search' onChange={(event)=>{setData(Preview=>({...Preview,[event.target.name]:[event.target.value]}))}} value={cherche.search}  />
          <button className="p-4 hover:bg-slate-600"type='button' onClick={handleFilter}  >clicker</button>

          <Resulte types={filtered}/>
      </form>

  )
}

export default SearchBar
