import React,{useEffect, useState} from "react";
import Button from '@mui/material/Button';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {  faFontAwesome, faFacebookSquare,faLinkedin,faTwitterSquare,faGithubSquare,faStackOverflow  } from '@fortawesome/free-brands-svg-icons'
import { library } from '@fortawesome/fontawesome-svg-core'
import { fas  } from '@fortawesome/free-solid-svg-icons'
import { faEnvelope , faComment , faRepeat , faHeart , faBarsStaggered,faArrowUpFromBracket } from '@fortawesome/free-solid-svg-icons'
library.add(fas, faFontAwesome)
import Image from "./images/comm-img.png"


// for users to post tweets.

function TweetForm(props){  
    
    return(
        <section className="bg-black text-gray-500 h-screen w-screen flex justify-center ">
            <img src={"#"} alt="#" />
            <div className="w-2/5 flex-col items-center justify-center ">
                <h3></h3>   {/*profile name*/}
                <span className="text-white"><p>@TeButt808</p>.<p>23h</p></span>
                <span className="text-white">React Native running on Apple Vision Pro simulator ðŸ¥½

                    It supports the native visionOS SDK rather than running the app in the compatibility mode called "Designed for iPad". You can tell it by the glassy effect on the rendered window. 

                    More details soonðŸš€</span>
                    <img className="main--img" src={image} alt="#" />
                    <div>  {/* this is for the comment section*/}

                    </div>
                    <div className="text-white-700 text-sm flex  justify-between  ">
                        <span className="flex items-center w-14  justify-space "  >
                            <FontAwesomeIcon icon={faComment} />{"8,661"}
                        </span>
                        <span className="flex items-center w-14  justify-space ">
                            <FontAwesomeIcon icon={faRepeat} />{"4,611"}
                        </span>
                        <span className="flex items-center w-14  justify-space ">
                            <FontAwesomeIcon icon={faHeart} />{"33.4K"}
                        </span>
                        <span className="flex items-center w-14  justify-space ">
                            <FontAwesomeIcon icon={faBarsStaggered} rotation={270} />{"3.2M"}
                        </span>
                        <span className=" items-center w-4   ">
                            <FontAwesomeIcon icon={faArrowUpFromBracket} />
                        </span>
                    </div>
                    
            </div>
        </section>
    )
};

export default TweetForm
