import { useState } from 'react';
import './App.css';
function App() {
 const [login,setLogin]=useState({
  email:"",password:"",newsletter:"" ,cPassword:"" ,join:false
 })
 function handleChange(event){
  const{name,value,checked,type}=event.target
  setLogin(prevForm=>{

    return{...prevForm,[name]:type==="checkbox"?checked:value}

  })
 }
 console.log(login.check)
 function handleSubmit(event){
  login.password===login.cPassword?console.log("success"):console.log("error")
  event.preventDefault()

 }

  return (
    <form onSubmit={handleSubmit} >
      <input type="email" name="email" value={login.email} placeholder='enter your email' onChange={handleChange}/>
      <input type="password" name="password" value={login.password} placeholder='enter a password' onChange={handleChange}/>
      <input type="password" name="cPassword" value={login.cPassword} placeholder='confirm your password' onChange={handleChange}/>
      <button>Submit</button>
      <input type="checkbox" name='join' checked={login.join} id='check' onChange={handleChange} />
      <label htmlFor="check">I want to join my new sletter</label>
    </form>
  )
};
export default App;
