import { useState } from 'react';
import './App.css';
function App() {
  const [formData,setformData]=useState(
    {firstName:"" ,lastName:"" ,email:"" ,text:"" ,isGood:true ,employement:"" ,favColor:""}
    )
  function handleChange(event){
    const {name,type,value,checked}=event.target
    setformData(prevForm=>{
      return{
        ...prevForm,[name]:type==="checkbox"?checked:value
      }
    })
  }
  function handleSubmit(event){
    event.preventDefault()
    console.log(formData)
  }
  return (
    <form onSubmit={handleSubmit}>
      <input type='text' placeholder='frstname' value={formData.firstName} name='firstName' onChange={handleChange}/>
      <input type='text' placeholder='lastname' value={formData.lastName} name='lastName' onChange={handleChange}/>
      <input type='email' placeholder='E-mail' value={formData.email} name='email' onChange={handleChange}/>
      <br />
      <textarea value={formData.text} name='text' onChange={handleChange}/>
      <br />
      <input type="checkbox" id="isGood" checked={formData.isGood} onChange={handleChange} name='isGood' />
      <label htmlFor="isGood">Are you gay??</label>
      <br />
      <fieldset>
        <legend>This is just for testing</legend>
        <input type="radio" name='employement' onChange={handleChange} checked={formData.employement=== "Unemployed"} id='unemployed' value="Unemployed" />
        <label htmlFor="unemployed">Unemployed</label>
        <br />
        <input type="radio" name='employement' onChange={handleChange} checked={formData.employement=== "Part-time"} id='part-time' value="Part-time" />
        <label htmlFor="part-time">Part-time</label>
        <br />
        <input type="radio" name='employement' onChange={handleChange} checked={formData.employement=== "Full-time"} id='full-time' value="Full-time"/>
        <label htmlFor="full-time">Full-time</label>
      </fieldset>
      <br />
      <label htmlFor="favColor">Select your favorite color</label>
      <br />
      <select id="favColor" value={formData.favColor} onChange={handleChange} name='favColor'>
        <option value="red">Red</option>
        <option value="orange">Orange</option>
        <option value="yellow">Yellow</option>
        <option value="green">Green</option>
        <option value="blue">Blue</option>
        <option value="violet">Violet</option>
      </select>
      <button>Submit</button>
    </form>


  )
};
export default App;
