import { useState } from 'react';
import './App.css';

function App() {
 const [login,setLogin]=useState({
  email:"",password:"",newsletter:"" ,cPassword:"" ,join:false
 })
 function handleChange(event){
  const{name,value,checked,type}=event.target
  setLogin(prevForm=>{

    return{...prevForm,[name]:type==="checkbox"?checked:value}

  })
 }
 console.log(login.join)
 function handleSubmit(event){
  login.password===login.cPassword?console.log("success"):console.log("error")
  event.preventDefault()


 }

  return (
    <form onSubmit={handleSubmit}  >
      <div className='form'>
          <input className='input' type="email" name="email" value={login.email} placeholder='enter your email' onChange={handleChange}/>

          <input className='input' type="password" name="password" value={login.password} placeholder='enter a password' onChange={handleChange}/>

          <input className='input' type="password" name="cPassword" value={login.cPassword} placeholder='confirm your password' onChange={handleChange}/>
          <div>
              <input type="checkbox" name='join' checked={login.join} id='check' onChange={handleChange} />

              <label htmlFor="check">I want to join my new sletter</label>
          </div>
          <button className='btn-submit'>Submit</button>
      </div>

    </form>
  )
};
export default App;
