import React,{useState} from "react";
import memeData from "./datameme"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {  faFontAwesome } from '@fortawesome/free-brands-svg-icons'
import { library } from '@fortawesome/fontawesome-svg-core'
import { fas } from '@fortawesome/free-solid-svg-icons'
import { faImage } from '@fortawesome/free-solid-svg-icons'
library.add(fas, faFontAwesome)
function Hero(){  
    const [allMemeImages,setImages]=useState(memeData)
    const [memeImage,SetImage]=useState({
        topText:"",
        bottomText:"",
        randomImage:"https://i.imgflip.com/30b1gx.jpg"
    })
    function memeGenerator(){
        const memeArr=  allMemeImages.data.memes
        const randomIndex=Math.floor(Math.random()*memeArr.length);
        const memesArr= memeArr[randomIndex].url
        SetImage(prevImage=>({
            ...prevImage,randomImage:memesArr
        }))
    }

    return(
        <div className="con1">
            <div>
                <input placeholder="upper text" className="type-in" type="text"/>
                <input placeholder="lower text" className="type-in" type="text"/>
            </div>
            <button onClick={memeGenerator} className="btn" href='/'>
                Get a new meme image
                <FontAwesomeIcon className="icon" icon={faImage}/>
      </button>
            <section className="card">
                <img src={memeImage.randomImage} alt={memeImage.randomImage}/>
            </section>
        </div>
        
    )
};

export default Hero
